                DyeHMIWrite(i_cupNo, 308, 308, ia_zero);

                _cup_Temps[i_cupNo - 1]._i_stress = 2;

                //复位占用标志位
                FADM_Object.Communal._fadmSqlserver.InsertRun("Dail", i_cupNo + "号配液杯泄压完成");

            }
            catch (Exception ex)
