                {
                    FADM_Object.Communal._b_stop = false;
                    //new Reset().MachineReset(0);
                    MyModbusFun.MyMachineReset(); //复位
                }

                else
                {
                    FADM_Object.Communal.WriteMachineStatus(8);

                    if (ex.Message.Equals("-2"))
